mapper.select:
select sido_code, sido_name
from sidocode
order by sido_code;

select distinct dong
from baseaddress
where dongcode = #{gugun}
order by dong;

select no, dong, AptName, code, buildYear, jibun, lat, lng
from houseinfo
where dong = #{dong}
order by AptName;

select lat, lng 
from baseaddress
where dong = #{dong};

select no, dong, AptName, dealAmount, dealYear, dealMonth, dealDay, area, floor, jibun
from housedeal
where dong = #{dong} and AptName=#{aptName}
order by AptName;
